package webDriverPackage;

public class Leapyear {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i =2026;
		{
			if (i%4==0)
			{
				System.out.println("February 29 which is leap year");	
			}
				
			else
				
			{
		System.out.println("February 28 which is not leap year");
		
		
		
	}
		}
	}
}
		

